import React, { Component } from 'react'
import './style.css'

class AddTodo extends Component {
  constructor (props) {
    super(props)
    this.state = {
      input: '',
      items: []
    }
  }

  handleInput (event) {
    this.setState({
      input: event.target.value
    })
  }

  handleSubmit (event) {
    const array = this.state.items
    const item = {
      id: (array.length > 0) ? array[array.length - 1].id + 1 : 1,
      text: this.state.input
    }
    this.setState(prevState => {
      return {
        items: prevState.items.concat(item)
      }
    })
    this.setState({ input: '' })
    event.preventDefault()
  }

  handleDelete (key) {
  let a=  this.state.items.filter(item => item.id !== key)
  }

  render () {
    return (
      <div>
        <header className='header'>
          <h1>Todo</h1>
          <form onSubmit={(event) => this.handleSubmit(event)}>
            <input type='text' className='input-box' placeholder='Enter a Task' onChange={(event) => this.handleInput(event)} value={this.state.input} />
            <button type='submit' className='add-btn'>Add</button>
          </form>
        </header>
        <main>
          <Todos todos={this.state.items} onDelete={(key) => this.handleDelete(key)} />
        </main>
      </div>

    )
  }
}

class Todos extends Component {

  handleDelete (key) {
    this.props.onDelete(key)
  }

  render () {
    const todos = this.props.todos
    const list = todos.map(item => {
      return <li key={item.id} className='todo-list'>
        <input type='checkbox' />
        {item.text}
        <button className='delete-btn' onClick={() => this.handleDelete(item.id)}>Delete</button>
             </li>
    })
    return (
      <ul>{list}</ul>
    )
  }
}

class TodoApp extends Component {
  render () {
    return (
      <div>
        <AddTodo />
      </div>

    )
  }
}

export default TodoApp
