import './style.css'

class Header extends Component {
  render () {
    return (
      <header className='header'>
        <h1>Todo</h1>
        <form onSubmit={this.addItem}>
            <input type='text' className='input-box' placeholder='Enter a Task' onChange={this.getInput}/>
            <button type='submit' className='add-btn'>Add</button>
          </form>
      </header>
    )
  }
}
