import React, { useState } from 'react'
import AddTodo from './AddTodo'
import Todo from './Todo'
import PopUpNote from './PopUpNote'
import './style.css'

function TodoApp () {
  const [input, setInput] = useState('')
  const [items, setItem] = useState([])

  const handleInput = event => setInput(event.target.value)

  const handleSubmit = event => {
    event.preventDefault()
    const item = {
      id: Date.now(),
      text: input,
      complete: false,
      note: false,
      noteText: ''
    }
    const str = item.text.trim()
    if (str === '') alert('Enter the values')
    else setItem([item, ...items])
    setInput('')
  }

  const handleDelete = key => {
    const newItems = items.filter(item => item.id !== key)
    setItem(newItems)
  }

  const handleCheckBox = key => {
    const newItems = items.map(item => {
      if (item.id === key) {
        item.complete = !item.complete
        return item
      }
      return item
    })
    setItem(newItems)
  }

  const handleUpdate = (key, event) => {
    const newItems = items.map(item => {
      if (item.id === key) {
        item.text = event.target.value
        return item
      }
      return item
    })
    setItem(newItems)
  }

  const handleNote = (key, event) => {
    const newItems = items.map(item => {
      if (item.id === key) {
        item.note = !item.note
        item.noteText = event.target.value
        return item
      }
      return item
    })
    setItem(newItems)
  }

  handleUpdateNote(event) 

  return (
    <div>
      <AddTodo input={input} onInput={event => handleInput(event)} onSubmit={event => handleSubmit(event)} />
      <Todo
        items={items} onDelete={(key) => handleDelete(key)}
        onCheckBox={(key) => handleCheckBox(key)} onUpdate={(key, event) => handleUpdate(key, event)}
        onPopUpNote={(key, event) => handleNote(key, event)}
      />
      {items.map(item => {
        return (item.note && <PopUpNote key={item.id} item={item} onHandleUpdateNote={event => handleUpdateNote(event)} />)
      })}
    </div>
  )
}

export default TodoApp
